const selectProvince=(provinceId)=>{const url=new URL(location.href)
    if(provinceId=='All'){url.searchParams.delete('provinceId')
        localStorage.removeItem('provinceId')
        location.href=url.href}else if(url.searchParams.get('provinceId')!=provinceId&&localStorage.getItem('provinceId')!=null){url.searchParams.set('provinceId',provinceId)
        location.href=url.href}}
selectProvince(localStorage.getItem('provinceId'))
